import {Component, ViewChild} from "@angular/core";
import {NgOptimizedImage} from "@angular/common";

@Component({
    selector: "main-header",
    standalone: true,
    template: `
        <header class="header">
            <h2 class="label">LAB 4</h2>
            <img ngSrc="../../../assets/header/images/main.svg" class="logo" alt="mainLogo" fill="">

            <button #expandButton (click)="expand()" type="button" class="expandAuthor">
                
                                <img #arrow ngSrc="{{arrowDown ? arrowDownUrl : arrowUpUrl}}" 
                                     id="arrow" alt="expandAuthorIcon" fill="">
            </button>

            <div #expanded class="infoText">
                <p style="margin-top: 13px;">Student:<a class="colourfulText">Kuznetsov Daniil</a></p>
                <p>Group:<a class="colourfulText">P3234</a></p>
                <p>Variant:<a class="colourfulText">55555</a></p>
                <br/>
            </div>

        </header>
    `,
    imports: [
        NgOptimizedImage
    ],
    styleUrls: ['../../../assets/header/header.css']
})

export class MainHeader {

    private username: string = "";

    @ViewChild('expanded', {static: false})
    public expanded: HTMLElement
    @ViewChild('arrow', {static: false})
    public arrow: HTMLElement
    @ViewChild('expandButton', {static: false})
    public expandButton: HTMLElement

    public arrowUpUrl: string = "./../../assets/header/images/arrow-up.svg";
    public arrowDownUrl: string = "./../../assets/header/images/arrow-down.svg";
    public arrowDown: boolean = false;

    public expand() {
        console.log(this.expanded);
        console.log(this.arrow);
        console.log(this.expandButton);

        if (this.expanded.style.maxHeight) {
            this.expanded.style.maxHeight = null;
            this.arrowDown = true;
        } else {
            this.arrowDown = false;
            this.expandButton.style.maxHeight = this.expanded.scrollHeight + "px";
        }
    }
}
