    public arrowUpUrl: string = "../../../assets/header/arrow-up.svg";
    public arrowDownUrl: string = "./../../assets/header/arrow-down.svg";
    public arrowDown: boolean = false;

    public expand() {
        console.log("Method expand invoked!");
