import {Component} from "@angular/core";
import {NgOptimizedImage} from "@angular/common";

@Component({
    selector: "main-footer",
    standalone: true,
    template: `
        <footer>
            &#169; Goovnocode inc. 2023
            <br/>
            <a href="../../../assets/footer/github.svg" target="_blank">
                <img ngSrc="../../../assets/footer/github.svg" class="github" alt="githubLogo" fill="">
            </a>
        </footer>
    `,
    imports: [
        NgOptimizedImage,
    ],
    styleUrls: ['./footer.component.css']
})

export class MainFooter {

}
